{"title":"OOTS Event Sequence Timeline","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "title": "Event Sequence Timeline",
  "data": {
    "url": {
      "%context%": true,
      "%timefield%": "@timestamp",
      "index": "oots-logs-*",
      "body": {
        "size": 500,
        "sort": [{"@timestamp": "asc"}],
        "_source": ["@timestamp", "event.action", "event.outcome", "log.logger", "oots.conversation.id", "test.path"]
      }
    },
    "format": {"property": "hits.hits"}
  },
  "transform": [
    {"calculate": "toDate(datum._source['@timestamp'])", "as": "timestamp"},
    {"calculate": "datum._source['event.action']", "as": "action"},
    {"calculate": "datum._source['event.outcome'] || 'unknown'", "as": "outcome"},
    {"calculate": "datum._source['log.logger'] || 'APP'", "as": "logger"},
    {"calculate": "datum._source['oots.conversation.id'] || 'unknown'", "as": "conversation_id"},
    {"calculate": "datum._source['test.path'] || 0", "as": "test_path"}
  ],
  "mark": {
    "type": "circle",
    "size": 200,
    "stroke": "black",
    "strokeWidth": 1
  },
  "encoding": {
    "x": {
      "field": "timestamp",
      "type": "temporal",
      "title": "Time",
      "axis": {"format": "%H:%M:%S"}
    },
    "y": {
      "field": "action",
      "type": "nominal",
      "title": "Event",
      "sort": null
    },
    "color": {
      "field": "outcome",
      "type": "nominal",
      "title": "Outcome",
      "scale": {
        "domain": ["success", "failure", "unknown"],
        "range": ["#54B399", "#E7664C", "#D3DAE6"]
      }
    },
    "shape": {
      "field": "logger",
      "type": "nominal",
      "title": "Logger"
    },
    "tooltip": [
      {"field": "timestamp", "type": "temporal", "title": "Time"},
      {"field": "action", "type": "nominal", "title": "Action"},
      {"field": "outcome", "type": "nominal", "title": "Outcome"},
      {"field": "logger", "type": "nominal", "title": "Logger"},
      {"field": "conversation_id", "type": "nominal", "title": "Conversation"},
      {"field": "test_path", "type": "quantitative", "title": "Test Path"}
    ]
  },
  "width": "container",
  "height": 400
}"}}
